<h2>Partner consultation</h2><h1><?php print $inquiry['inquiry'] -> title; ?> </h1><h2>Inquiry</h2><?php// Link to xml download of costsif (!empty($inquiry['inquiry'] -> field_cost_period)) {  $consult_number = $inquiry['inquiry'] -> field_consult_number['und'][0]['value'];  print l(t('Download costs as xml file'), 'consultation2xml/' . $consult_number . '/xml');}?>
<table class='consultation-table'><tr><td class='consultation-table-first-column'><?php print $inquiry['partner_acronym']; ?></td><td>
<?php   $lang = key($inquiry['inquiry'] -> body);  print nl2br($inquiry['inquiry'] -> body[$lang][0]['value']);   if (!empty($inquiry['inquiry'] -> field_files)) {    print '<br><em>Documents:</em>';    foreach ($inquiry['inquiry'] -> field_files['und'] as $file) {	  print ' ' . l($file['description'], file_create_url($file['uri']));	}  }   if (!empty($inquiry['inquiry'] -> field_delivery_date)) {    print '<br><em>Deadline: </em>';	print substr($inquiry['inquiry'] -> field_delivery_date['und'][0]['value'],0,10);  }   if ($inquiry['edit_access']) print '<br>' . l('Edit', 'node/'. $inquiry['inquiry'] -> nid . '/edit/', array('query' => drupal_get_destination()) );?>
</td></table><h2>Responses</h2><table class='consultation-table'>
<?php  foreach($responses as $row_count => $row) {  print '<tr';	if ($row_count%2) print ' class="even"'; else print ' class="odd"';	print '>';	print '<td class="consultation-table-first-column">' . $row['partner_acronym'] . '</td>';	print '<td>';	$form_path = 'response/' . $row['participant_nid'] . '/' . $row['consult_number'] . '/' . $row['project_nid'] . '/';	if (!empty($row['response'])) {	  $lang = $row['response'] -> language;	  if (!empty($row['response'] -> body)) print str_replace('<p> </p>', '', $row['response'] -> body[$lang][0]['safe_value']);      if (!empty($row['response'] -> field_files)) {        print '<br><em>Documents:</em>';        foreach ($row['response'] -> field_files['und'] as $file) {	      print ' ' . l($file['description'], file_create_url($file['uri'])) . ';';	    }      }      if (!empty($row['response'] -> field_cost_total)) {	    print '<p>Total costs: &euro;' . $row['response'] -> field_cost_total['und'][0]['value'] . '</p>';	  	  }	  if ($row['edit_access']) print '<p>' . l('Edit', 'node/'. $row['response_nid'] . '/edit/' .  $form_path, array('query' => drupal_get_destination()) ) . '</p>';	}	else {	  if ($row['edit_access']) print l('Respond', 'node/add/consult/' .  $form_path, array('query' => drupal_get_destination()) );	}	print '</td>';    print '</tr>';  }?>
</table>
